import React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import IconButton from '@material-ui/core/IconButton';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import MailIcon from '@material-ui/icons/Mail';
import MenuIcon from '@material-ui/icons/Menu';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import AcUnitIcon from '@material-ui/icons/AcUnit';
import Payment from '@material-ui/icons/Payment';
import ListAltIcon from '@material-ui/icons/ListAlt';
import AssignmentIndIcon from '@material-ui/icons/AssignmentInd';
import InsertChartIcon from '@material-ui/icons/InsertChart';
import { Link } from 'react-router-dom';
import { useContext } from "react";
import Auth from "../contexts/Auth";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    drawer: {
        [theme.breakpoints.up('sm')]: {
            width: drawerWidth,
            flexShrink: 0,
        },
    },
    appBar: {
        [theme.breakpoints.up('sm')]: {
            width: `calc(100% - ${drawerWidth}px)`,
            marginLeft: drawerWidth,
        },
    },
    menuButton: {
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('sm')]: {
            display: 'none',
        },
    },
    // necessary for content to be below app bar
    toolbar: theme.mixins.toolbar,
    drawerPaper: {
        width: drawerWidth,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));

function ResponsiveDrawer(props) {
    const { isAuthenticated } = useContext(Auth);
    const { window } = props;
    const classes = useStyles();
    const theme = useTheme();
    const [mobileOpen, setMobileOpen] = React.useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    };

    const drawer = (
        <div>
            <div className={classes.toolbar} />

            <List>
                <ListItem button>
                    <ListItemIcon><Link to='/Listefiliere' ><AcUnitIcon /></Link></ListItemIcon>
                    <Link to='/Listefiliere' ><ListItemText primary={'Listefiliere'} /></Link>
                </ListItem>
                <ListItem button>
                    <ListItemIcon><Link to='/ListeEleveEconoma' ><Payment /></Link></ListItemIcon>
                    <Link to='/ListeEleveEconoma' ><ListItemText primary={'Economa'} /></Link>
                </ListItem>
                <ListItem button>
                    <ListItemIcon><Link to='/ListeEleve' ><Payment /></Link></ListItemIcon>
                    <Link to='/ListeEleve' ><ListItemText primary={'Eleve'} /></Link>
                </ListItem>
                <ListItem button>
                    <ListItemIcon><Link to='/ListeEleve' ><InsertChartIcon /></Link></ListItemIcon>
                    <Link to='/ListeEleve' ><ListItemText primary={'Statistique'} /></Link>
                </ListItem>
                <ListItem button>
                    <ListItemIcon><Link to='/ListeEleve' ><AssignmentIndIcon /></Link></ListItemIcon>
                    <Link to='/ListeEleve' ><ListItemText primary={'Enseignant'} /></Link>
                </ListItem>
            </List>
        </div>
    );

    const container = window !== undefined ? () => window().document.body : undefined;

    return (
        <div className={classes.root}>
            <CssBaseline />
            <AppBar position="fixed" className={classes.appBar}>
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="open drawer"
                        edge="start"
                        onClick={handleDrawerToggle}
                        className={classes.menuButton}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" noWrap>
                        Se-koly
                    </Typography>
                </Toolbar>
            </AppBar>
            {(isAuthenticated && (<nav className={classes.drawer} aria-label="mailbox folders">
                {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
                <Hidden smUp implementation="css">
                    <Drawer
                        container={container}
                        variant="temporary"
                        anchor={theme.direction === 'rtl' ? 'right' : 'left'}
                        open={mobileOpen}
                        onClose={handleDrawerToggle}
                        classes={{
                            paper: classes.drawerPaper,
                        }}
                        ModalProps={{
                            keepMounted: true, // Better open performance on mobile.
                        }}
                    >
                        {drawer}
                    </Drawer>
                </Hidden>
                <Hidden xsDown implementation="css">
                    <Drawer
                        classes={{
                            paper: classes.drawerPaper,
                        }}
                        variant="permanent"
                        open
                    >
                        {drawer}
                    </Drawer>
                </Hidden>
            </nav>))}

        </div>
    );
}

ResponsiveDrawer.propTypes = {
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};

export default ResponsiveDrawer;
