import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from "react-router-dom";
import { base_url } from '../../environnement/environnement';
import { getItem } from '../../services/LocalStorage';
import Table from 'react-bootstrap/Table';
import EleveService from '../../services/ServicesEleve';
import './filiere.css';


export default function ListeEleveFiliere() {
    const location = useLocation();
    const [eleve, setEleve] = useState([]);
    const [note, setNote] = useState([]);


    // event.preventDefault();
    useEffect(() => {
        let state = location?.state;
        let id_filiere = state?.filiere;
        let id_niveau = state?.niveau;
        let session_id = state?.sess;
        console.log(state);


        EleveService.getEleveByClasse(id_niveau, id_filiere, session_id)
            .then(res => {
                const listeEleve = res.data.eleve;
                setEleve({ listeEleve });
                console.log(res.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
    }, [])

    return (
        <div className="wrapper">
            <Table striped bordered hover variant="blue">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Nom</th>
                        <th></th>
                        {/* <th>Filiere</th>
                        <th>Niveau</th>
                        <th>Groupe</th> */}
                    </tr>
                </thead>
                <tbody>
                    {eleve?.listeEleve?.map(el => < tr >
                        <td key={{ el }} >{el.num_matricule}</td>
                        <td >{el.first_name}</td>
                        <td><input type="text" name='FirstName' onChange={e => setFirstName(e.target.value)} /></td>
                        {/* <td >{el.Filiere}</td>
                        <td >{el.Niveau}</td>
                        <td >{el.Groupe}</td> */}
                    </tr>)}
                </tbody>
            </Table >
        </div>
    )
}
