import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from "react-router-dom";
import { base_url } from '../../environnement/environnement';
import { getItem } from '../../services/LocalStorage';
import Table from 'react-bootstrap/Table';


export default function ListeEleveFiliere() {
    const location = useLocation();
    const [eleve, setEleve] = useState([]);


    // event.preventDefault();
    useEffect(() => {
        let state = location?.state;
        let id_filiere = state?.filiere_id;
        let id_niveau = state?.niveau_id;
        let id_groupe = state?.groupe_id;
        console.log(state);

        var bodyFormData = new FormData();
        const token = getItem('miniblogToken');
        bodyFormData.append('id_filiere', id_filiere);
        bodyFormData.append('id_niveau', id_niveau);
        bodyFormData.append('id_groupe', id_groupe);

        axios({
            method: "post",
            url: base_url + 'api/FiliereEleve/getByFiliere',
            data: bodyFormData,
            headers: {
                "Content-Type": "multipart/form-data",
                Authorization: `Bearer ${token}`
            },
        })
            .then(res => {
                const listeEleve = res.data.result;
                setEleve({ listeEleve });
                console.log(listeEleve);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
    }, [])

    return (
        <div className="login-wrapper">
            <Table striped bordered hover variant="dark">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Nom</th>
                        <th>Filiere</th>
                        <th>Niveau</th>
                        <th>Groupe</th>
                    </tr>
                </thead>
                <tbody>
                    {eleve?.listeEleve?.map(el => < tr >
                        <td key={{ el }} >{el.id}</td>
                        <td >{el.Eleve}</td>
                        <td >{el.Filiere}</td>
                        <td >{el.Niveau}</td>
                        <td >{el.Groupe}</td>
                    </tr>)}
                </tbody>
            </Table >
        </div>
    )
}
