import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import ServiceFiliere from '../../services/ServiceFiliere';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import { useHistory } from 'react-router-dom';
import { Redirect } from 'react-router-dom';
import Auth from '../../contexts/Auth';
import { useContext } from "react";

export default function Listefiliere() {
    const [Filiere, setFiliere] = useState([]);
    const history = useHistory();
    const { isAuthenticated } = useContext(Auth);
    useEffect(() => {
        if (!isAuthenticated) {
            ServiceFiliere.getFiliere()
                .then(res => {

                    const pays = res.data.Filiere;
                    setFiliere({ pays });
                    console.log(res.data.Filiere);
                })
                .catch(function (error) {
                    // handle error
                    console.log(error);
                });

        }
        history.push('/login')

    }, []);
    const navigateUpdate = (id) => {
        history.push(
            '/listeNiveau',
            {
                filiere_id: id,
            }
        )
    }
    return (
        <>
            <h1> Les filieres</h1>
            <Container>
                <Navbar expand="lg" variant="light" bg="light">
                    <Container>
                        <Navbar.Brand href="/NouveauFiliere">Insert Filier</Navbar.Brand>
                    </Container>
                </Navbar>
            </Container>
            <Table striped bordered hover variant="dark">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Filiere</th>

                    </tr>
                </thead>
                <tbody>
                    {Filiere?.pays?.map(fil => < tr >
                        <td key={{ fil }} >{fil.id}</td>
                        <td >{fil.Filiere}</td>
                        <td><Button variant="outline-info" onClick={() => navigateUpdate(fil.id)}>Modifier</Button></td>
                    </tr>)}
                </tbody>
            </Table >
        </>

    )
}
