import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import axios, { axiosProvider } from 'axios';
import { useHistory } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Service from '../../services/ServiceFiliere';
import { useLocation } from "react-router-dom";
import { base_url } from '../../environnement/environnement';
import { getItem } from '../../services/LocalStorage';
import Table from 'react-bootstrap/Table';


export default function InsertEleve() {
    const location = useLocation();
    const history = useHistory();
    const [Filiere, setFiliere] = useState([]);
    const [id, setId] = useState();
    // event.preventDefault();
    useEffect(() => {
        let state = location?.state;
        let id_filiere = state?.filiere_id;
        setId(id_filiere);
        var bodyFormData = new FormData();
        const token = getItem('miniblogToken');
        bodyFormData.append('id_filiere', id_filiere);
        axios({
            method: "post",
            url: base_url + 'api/classe/getByFiliere',
            data: bodyFormData,
            headers: {
                "Content-Type": "multipart/form-data",
                Authorization: `Bearer ${token}`
            },
        })
            .then(res => {
                const listeNiveau = res.data.result;
                setFiliere({ listeNiveau });
                console.log(listeNiveau);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
    }, [])
    const navigateUpdate = (id_filiere, id_niveau, id_groupe) => {
        history.push(
            '/ListeEleveFiliere',
            {
                filiere_id: id_filiere,
                niveau_id: id_niveau,
                groupe_id: id_groupe
            }
        )
    }

    return (
        <div className="login-wrapper">
            <h1>filiere :{Filiere[0]?.listeNiveau[0]?.Filiere}</h1>
            <Table striped bordered hover variant="dark">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Filiere</th>
                        <th>Niveau</th>
                        <th>Groupe</th>
                    </tr>
                </thead>
                <tbody>
                    {Filiere?.listeNiveau?.map(fil => < tr >
                        <td key={{ fil }} >{fil.id}</td>
                        <td >{fil.Filiere}</td>
                        <td >{fil.Niveau}</td>
                        <td >{fil.groupe}</td>
                        <td><Button variant="outline-info" onClick={() => navigateUpdate(fil.filiere_id, fil.niveau_id, fil.groupe_id)}>Liste Eleve</Button></td>
                    </tr>)}
                </tbody>
            </Table >
        </div>
    )
}
