                        <Container>
                            <Navbar.Brand href="/NouveauFiliere">Insert Filier</Navbar.Brand>
                        </Container>
                    </Navbar>
                </Container>
                <Table >
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Filiere</th>
                            <th>session</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {Matiere?.matieres?.map(mat => < tr >
                            <td >{mat.id}</td>
                            <td >{mat.code_matiere}</td>
