                                <td> <select onChange={e => setSession_id(e.target.value)}>
                                    {session?.sessions?.map(sess =>
                                        <option value={sess.id}>{sess.date_debut}//{sess.date_fin}</option>
                                    )}
                                </select></td>
                            </label>
                            <td><Button variant="outline-info" onClick={() => navigateUpdate(mat.id_filiere, mat.id_niveau)}>ajouter note</Button></td>
                        </tr>)}
                    </tbody>
                </Table >
            </div>
        </>

    )
}
