import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useLocation } from "react-router-dom";
import { base_url } from '../../environnement/environnement';
import { getItem } from '../../services/LocalStorage';
import Table from 'react-bootstrap/Table';
import EleveService from '../../services/ServicesEleve';
import './filiere.css';


export default function ListeEleveFiliere() {
    const location = useLocation();
    const [eleve, setEleve] = useState([]);
    const [note, setNote] = useState([]);
    const [taille, setTaille] = useState(1);
    const [addData, setAddData] = useState({});

    // event.preventDefault();
    useEffect(() => {
        let state = location?.state;
        let id_filiere = state?.filiere;
        let id_niveau = state?.niveau;
        let session_id = state?.sess;
        console.log(state);


        EleveService.getEleveByClasse(id_niveau, id_filiere, session_id)
            .then(res => {
                const listeEleve = res.data.eleve;
                setEleve({ listeEleve });
                console.log(res.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
    }, [])
    const handleSubmit = async e => {
        e.preventDefault();
        console.log(note[1]);
        // ServiceFiliere.insertFiliere(filiere)
        //     .then(function (response) {
        //         //handle success
        //         console.log(response);
        //     })
        //     .catch(function (response) {
        //         //handle error
        //         console.log(response);
        //     })
        // console.log(filiere);
        // history.push("/Listefiliere");
    }

    const handleChange = useCallback((e) => {
        const { name, value } = e.target;
        console.log(name, value);
        setAddData((s) => ({
            ...s,
            [name]: value,
        }));
    }, []);


    console.log(addData);


    return (
        <div className="wrapper">
            <form onSubmit={handleSubmit}>
                <Table striped bordered hover variant="blue">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Nom</th>

                            <th>note</th>
                            {/* <th>Niveau</th>
                        <th>Groupe</th>  */}
                        </tr>
                    </thead>
                    <tbody>
                        {eleve?.listeEleve?.map((el, index) => < tr >
                            <td key={{ el }} >{el.num_matricule} </td>
                            <td >{el.first_name}</td>
                            <td><input type="text" name={`note_${index}_${el.num_matricule}`} onChange={handleChange} /></td>
                        </tr>)}
                    </tbody>
                </Table >
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
