import React, { useState, useEffect } from 'react';
import ServicesEleve from '../../services/ServicesEleve';
import InsertFiliereEleve from './InsertFiliereEleve';
import { Container, Row, Col } from 'react-bootstrap';
import './economa.css';
import Button from 'react-bootstrap/Button';

export default function InsertEleve() {
    const [modalShow, setModalShow] = useState(false);
    const [nationalite, setNationalite] = useState([]);
    const [nationality_id, setNationality_id] = useState();
    const [num_matricule, setNum_matricule] = useState();
    const [first_name, setFirst_name] = useState();
    const [last_name, setLast_name] = useState();
    const [birthday, setBirthday] = useState();
    const [birthday_place, setBirthday_place] = useState();
    const [profil_photo, setProfil_photo] = useState();
    const [bacc, setBacc] = useState();
    const [graduation_year, setGraduation_year] = useState();
    const [telephone, setTelephone] = useState();
    const [adress_email, setAdress_email] = useState();
    const [adress, setAdress] = useState();
    const [genre, setGenre] = useState();
    const [father_name, setFather_name] = useState();
    const [father_phone, setFather_phone] = useState();
    const [father_occupation, setFather_occupation] = useState();
    const [mother_name, setMother_name] = useState();
    const [mother_phone, setMother_phone] = useState();
    const [mother_occupation, setMother_occupation] = useState();
    const [tutor_name, setTutor_name] = useState();
    const [tutor_occupation, setTutor_occupation] = useState();
    const [tutor_phone, setTutor_phone] = useState();
    // event.preventDefault();
    useEffect(() => {
        ServicesEleve.getNationalite()
            .then(res => {
                const pays = res.data.Nationalite;
                setNationalite({ pays });
                console.log(res.data.Nationalite);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });


    }, []);
    const handleSubmit = async e => {
        e.preventDefault();
        var bodyFormData = new FormData();
        bodyFormData.append('nationality_id', nationality_id);
        bodyFormData.append('num_matricule', num_matricule);
        bodyFormData.append('first_name', first_name);
        bodyFormData.append('last_name', last_name);
        bodyFormData.append('birthday', birthday);
        bodyFormData.append('birthday_place', birthday_place);
        bodyFormData.append('profil_photo', profil_photo);
        bodyFormData.append('bacc', bacc);
        bodyFormData.append('graduation_year', graduation_year);
        bodyFormData.append('telephone', telephone);
        bodyFormData.append('adress_email', adress_email);
        bodyFormData.append('adress', adress);
        bodyFormData.append('genre', genre);
        bodyFormData.append('father_name', father_name);
        bodyFormData.append('father_phone', father_phone);
        bodyFormData.append('father_occupation', father_occupation);
        bodyFormData.append('mother_name', mother_name);
        bodyFormData.append('mother_phone', mother_phone);
        bodyFormData.append('mother_occupation', mother_occupation);
        bodyFormData.append('tutor_name', tutor_name);
        bodyFormData.append('tutor_occupation', tutor_occupation);
        bodyFormData.append('tutor_phone', tutor_phone);
        setModalShow(true);
        ServicesEleve.insertEleve(bodyFormData)
            .then(function (response) {
                //handle success
                console.log(response);
            })
            .catch(function (response) {
                //handle error
                console.log(response);
            })
        // history.push("/");
    }
    return (
        <>
            <div className="wrapper">
                < Container>
                    <Row>
                        <Col xs={12} md={2}>
                        </Col>
                        <Col xs={12} md={10}>
                            <h1>Nouveau Filiere</h1>
                            <form onSubmit={handleSubmit}>
                                <Col xs={12} md={4}>

                                </Col>
                                <Col xs={12} md={8}>

                                </Col>

                                <Row className="justify-content-md-center">
                                    <Col xs={12} md={4}>
                                        <label><h5>Nationalite : </h5> </label><br /><br />
                                        <label> <h5>num_matricule : </h5></label><br /><br />
                                        <label>   <h5>First_name : </h5></label><br /><br />
                                        <label>   <h5>last_name :</h5></label><br /><br />
                                        <label>  <h5>Birthday :</h5></label><br /><br />
                                        <label>   <h5>Birthday_place :</h5></label><br /><br />
                                        <h5>Profil_photo :</h5><br></br>
                                        <h5>Bacc :</h5><br></br>
                                        <h5>Graduation_year :</h5><br></br>
                                        <h5>Telephone :</h5><br></br>
                                        <h5>Adress_email :</h5><br></br>
                                        <h5>Adress :</h5><br></br>
                                        <h5>Genre :</h5><br></br>
                                        <h5>Father_name :</h5><br></br>
                                        <h5>Father_phone :</h5><br></br>
                                        <h5>Father_occupation :</h5><br></br>
                                        <h5>Mother_name :</h5><br></br>
                                        <h5>Mother_phone :</h5><br></br>
                                        <h5>Mother_occupation :</h5><br></br>
                                        <h5>Tutor_name :</h5><br></br>
                                        <h5>tutor_occupation :</h5><br></br>
                                        <h5>Tutor_phone ;</h5><br></br>
                                    </Col>

                                    <Col xs={12} md={8}>
                                        <select onChange={e => setNationality_id(e.target.value)}>
                                            {nationalite?.pays?.map(nation =>
                                                <option value={nation.id}>{nation.nationalite} </option>
                                            )}
                                        </select><br /><br />
                                        <input type="text" name='num_matricule' onChange={e => setNum_matricule(e.target.value)} /><br /> <br />
                                        <input type="text" name='First_name' onChange={e => setFirst_name(e.target.value)} /><br /><br />
                                        <input type="text" name='last_name' onChange={e => setLast_name(e.target.value)} /><br /><br />
                                        <input type="date" name='Birthday' onChange={e => setBirthday(e.target.value)} /><br /><br />
                                        <input type="text" name='Birthday_place' onChange={e => setBirthday_place(e.target.value)} /><br /><br />
                                        <input type="text" name='Profil_photo' onChange={e => setProfil_photo(e.target.value)} /><br /><br />
                                        <input type="text" name='Bacc' onChange={e => setBacc(e.target.value)} /><br /><br />
                                        <input type="date" name='Graduation_year' onChange={e => setGraduation_year(e.target.value)} /><br /><br />
                                        <input type="text" name='Telephone' onChange={e => setTelephone(e.target.value)} /><br /><br />
                                        <input type="text" name='Adress_email' onChange={e => setAdress_email(e.target.value)} /><br /><br />
                                        <input type="text" name='Adress' onChange={e => setAdress(e.target.value)} /><br /><br />
                                        <select onChange={e => setGenre(e.target.value)}>
                                            <option value="true">Feminin</option>
                                            <option value="false">masculin</option>
                                        </select><br /><br /><br />
                                        <input type="text" name='Father_name' onChange={e => setFather_name(e.target.value)} /><br /><br />
                                        <input type="text" name='Father_phone' onChange={e => setFather_phone(e.target.value)} /><br /><br />
                                        <input type="text" name='Father_occupation' onChange={e => setFather_occupation(e.target.value)} /><br /><br />
                                        <input type="text" name='Mother_name' onChange={e => setMother_name(e.target.value)} /><br /><br />
                                        <input type="text" name='Mother_phone' onChange={e => setMother_phone(e.target.value)} /><br /><br />
                                        <input type="text" name='Mother_occupation' onChange={e => setMother_occupation(e.target.value)} /><br /><br />
                                        <input type="text" name='Tutor_name' onChange={e => setTutor_name(e.target.value)} /><br /><br />
                                        <input type="text" name='Tutor_occupation' onChange={e => setTutor_occupation(e.target.value)} /><br /><br />
                                        <input type="text" name='Tutor_phone' onChange={e => setTutor_phone(e.target.value)} /><br /><br />
                                    </Col>
                                </Row>
                                <div>
                                    <Button type="submit">Submit</Button>
                                </div>
                            </form>
                        </Col>

                    </Row>
                </Container>

            </div >
            <InsertFiliereEleve show={modalShow} onHide={() => setModalShow(false)} matricule={num_matricule} />
        </>
    )
}
