import axios from "axios";
import { base_url } from "../environnement/environnement";
import { getItem } from "./LocalStorage";

export default class EleveService {
    static getNationalite = async () => {
        const token = getItem('miniblogToken');
        return axios.get(base_url + 'api/nationalite/getNationalite', {
            headers: { Authorization: `Bearer ${token}` }
        });
    }
    static insertEleve = async (bodyFormData) => {
        const token = getItem('miniblogToken');
        return axios({
            method: "post",
            url: base_url + 'api/Eleve/insert',
            data: bodyFormData,
            headers: {
                "Content-Type": "multipart/form-data",
                Authorization: `Bearer ${token}`
            },
        });
    }
    static getByMatricule = async (bodyFormData) => {
        const token = getItem('miniblogToken');
        return axios({
            method: "post",
            url: base_url + 'api/Eleve/getBymatricule',
            data: bodyFormData,
            headers: {
                "Content-Type": "multipart/form-data",
                Authorization: `Bearer ${token}`
            },
        });
    }
    static insertFiliereEleve = async (bodyFormData) => {
        const token = getItem('miniblogToken');
        return axios({
            method: "post",
            url: base_url + 'api/FiliereEleve/insert',
            data: bodyFormData,
            headers: {
                "Content-Type": "multipart/form-data",
                Authorization: `Bearer ${token}`
            },
        });
    }
    static getAllFiliereEleve = async () => {
        const token = getItem('miniblogToken');
        return axios.get(base_url + 'api/FiliereEleve/getFiliere', {
            headers: { Authorization: `Bearer ${token}` }
        });
    }
    static getMois = async () => {
        return axios.get(base_url + 'mois/getmois');
    }
    static getMois = async () => {
        return axios.get(base_url + 'api/FiliereEleve/getFiliere');
    }
}
