import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import ServiceFiliere from '../../services/ServiceFiliere';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import { useHistory } from 'react-router-dom';
import { useLocation } from 'react-router';
import './filiere.css';

export default function ListeMatiere() {
    const [Matiere, setMatiere] = useState([]);
    const [session, setSession] = useState([]);
    const [session_id, setSessionId] = useState();
    const [filiere_id, setFiliereId] = useSate();
    const [niveau_id, setSession_id] = useState();
    const history = useHistory();
    const location = useLocation();

    useEffect(() => {
        let state = location?.state;
        let id_filiere = state?.id_Niveau_Filiere;
        ServiceFiliere.getMatiere(id_filiere)
            .then(res => {

                const matieres = res.data.matiere;
                setMatiere({ matieres });
                console.log(res.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
        ServiceFiliere.getSession()
            .then(res => {

                const sessions = res.data.session;
                setSession({ sessions });
                console.log(res.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });


    }, []);
    const navigateUpdate = (id_filiere, id_niveau) => {
        history.push(
            '/listeNiveau',
            {
                filiere: filiere_id,
                niveau: niveau_id,
                session: session_id
            }
        )
    }
    const handleSubmit = async e => {
        e.preventDefault();
