        <>
            <div className="wrapper">
                <h1> Les filieres</h1>
                <Container>
                    <Navbar expand="lg" variant="light" bg="light">
                        <Container>
                            <Navbar.Brand href="/NouveauFiliere">Insert Filier</Navbar.Brand>
                        </Container>
                    </Navbar>
                </Container>
                <Table >
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Filiere</th>
                            <th>session</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {Matiere?.matieres?.map(mat => < tr >
                            <form onSubmit={handleSubmit}>
                                <td >{mat.id}</td>
                                <td >{mat.code_matiere}</td>
                                <label>

                                    <td> <select onChange={e => setSessionId(e.target.value)}>
                                        {session?.sessions?.map(sess =>
                                            <option value={sess.id}>{sess.date_debut}//{sess.date_fin}</option>
                                        )}
                                    </select></td>
                                </label>
                                <td><Button variant="outline-info" onClick={() => navigateUpdate(mat.id_filiere, mat.id_niveau)}>ajouter note</Button></td>
                            </form>
                        </tr>)}
                    </tbody>
                </Table >
            </div>
        </>

    )
}
